mapper.select:
SELECT * FROM (
	SELECT rownum rnum, D.* FROM (
		SELECT
			dno, adminid, dtitle, dcontent
			, dhit, dwritedate, dlike
		FROM doginfo
		ORDER BY dno DESC
	) D
) DOGINFO
WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE doginfo
SET dhit = dhit + 1
WHERE dno = #{dno}

UPDATE doginfo
SET dtitle = #{dtitle}
	, dcontent = #{dcontent}
WHERE dno = #{dno }
