mapper.select:
SELECT * FROM (
	SELECT rownum rnum, R.* FROM (
		SELECT
			rno, title, content , userid, hit, write_date
		FROM recommendboard
		ORDER BY rno DESC
	) R
) recommendboard
WHERE rnum BETWEEN #{startNo } AND #{endNo }

mapper.update:
UPDATE recommendboard
SET hit = hit+1
WHERE rno = #{rno}
